import{_ as e,c as r,o,ag as a}from"./chunks/framework.CI6Ra_OS.js";const p=JSON.parse('{"title":"Software Overview","description":"","frontmatter":{"prev":{"text":"Architecture","link":"/architecture/"},"next":{"text":"Infrastructure","link":"/components/infrastructure"}},"headers":[],"relativePath":"components/index.md","filePath":"components/index.md"}'),n={name:"components/index.md"};function i(s,t,d,l,c,g){return o(),r("div",null,[...t[0]||(t[0]=[a('<h1 id="software-overview" tabindex="-1">Software Overview <a class="header-anchor" href="#software-overview" aria-label="Permalink to &quot;Software Overview&quot;">​</a></h1><p>The homelab runs a variety of infrastructure components and applications, all managed through GitOps with ArgoCD.</p><h2 id="infrastructure-layer" tabindex="-1">Infrastructure Layer <a class="header-anchor" href="#infrastructure-layer" aria-label="Permalink to &quot;Infrastructure Layer&quot;">​</a></h2><p>These components form the foundation of the cluster. See the <a href="/components/infrastructure.html">Infrastructure</a> page for details.</p><table tabindex="0"><thead><tr><th>Component</th><th>Purpose</th></tr></thead><tbody><tr><td><strong>K3s</strong></td><td>Lightweight Kubernetes distribution optimized for edge and IoT</td></tr><tr><td><strong>Longhorn</strong></td><td>Cloud-native distributed block storage for Kubernetes</td></tr><tr><td><strong>Traefik</strong></td><td>Modern reverse proxy and ingress controller with automatic TLS</td></tr><tr><td><strong>ArgoCD</strong></td><td>Declarative GitOps continuous delivery for Kubernetes</td></tr><tr><td><strong>cert-manager</strong></td><td>Automatic TLS certificate provisioning via Let&#39;s Encrypt</td></tr><tr><td><strong>Sealed Secrets</strong></td><td>Encrypted secret storage safe for Git repositories</td></tr></tbody></table><h2 id="observability-stack" tabindex="-1">Observability Stack <a class="header-anchor" href="#observability-stack" aria-label="Permalink to &quot;Observability Stack&quot;">​</a></h2><p>Full-stack monitoring and alerting. See the <a href="/components/observability.html">Observability</a> page for details.</p><table tabindex="0"><thead><tr><th>Component</th><th>Description</th></tr></thead><tbody><tr><td><strong>Prometheus</strong></td><td>Time-series metrics collection with 60-second scrape intervals and persistent storage</td></tr><tr><td><strong>Grafana</strong></td><td>Visualization platform with pre-built dashboards for cluster monitoring</td></tr><tr><td><strong>Loki</strong></td><td>Log aggregation system using label-based querying similar to Prometheus</td></tr><tr><td><strong>Alertmanager</strong></td><td>Alert routing and notification management with Discord integration</td></tr><tr><td><strong>Observium</strong></td><td>SNMP-based network monitoring for switches and infrastructure devices</td></tr></tbody></table><h2 id="ai-inference" tabindex="-1">AI Inference <a class="header-anchor" href="#ai-inference" aria-label="Permalink to &quot;AI Inference&quot;">​</a></h2><p>Local LLM and generative AI serving. See the <a href="/components/ai-inference.html">AI Inference</a> page for details.</p><table tabindex="0"><thead><tr><th>Component</th><th>Description</th></tr></thead><tbody><tr><td><strong>llama.cpp</strong></td><td>LLM inference engine with ROCm GPU acceleration</td></tr><tr><td><strong>vLLM</strong></td><td>High-throughput model serving on CUDA</td></tr><tr><td><strong>LiteLLM</strong></td><td>Unified OpenAI-compatible API gateway for all models</td></tr><tr><td><strong>Open WebUI</strong></td><td>ChatGPT-style web interface for local models</td></tr><tr><td><strong>ComfyUI</strong></td><td>Node-based image generation workflows</td></tr><tr><td><strong>Speaches</strong></td><td>Text-to-speech and audio transcription</td></tr></tbody></table><h2 id="applications" tabindex="-1">Applications <a class="header-anchor" href="#applications" aria-label="Permalink to &quot;Applications&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Application</th><th>Description</th></tr></thead><tbody><tr><td><strong>Home Assistant</strong></td><td>Smart home automation platform with Zigbee device support and voice control</td></tr><tr><td><strong>Omada Controller</strong></td><td>TP-Link network management for switches and access points</td></tr><tr><td><strong>Unifi Controller</strong></td><td>Ubiquiti network management running on a dedicated node</td></tr><tr><td><strong>Semaphore</strong></td><td>Web-based UI for running Ansible playbooks and managing automation jobs</td></tr><tr><td><strong>Kiwix</strong></td><td>Offline content server for Wikipedia and other reference material</td></tr></tbody></table><div class="info custom-block"><p class="custom-block-title">Deployment Patterns</p><ul><li><strong>Deployment + Recreate</strong>: Apps with RWO PVCs (prevents Multi-Attach errors)</li><li><strong>StatefulSet</strong>: Databases (MongoDB, PostgreSQL, MariaDB, Prometheus)</li><li><strong>CronJob</strong>: Backup jobs (etcd, MongoDB, PostgreSQL)</li><li><strong>Java apps</strong> (Omada/Unifi): Need 2000m CPU at startup, use <code>hostNetwork=true</code></li></ul></div>',14)])])}const u=e(n,[["render",i]]);export{p as __pageData,u as default};
